(128, 0, 'Services: Chart JS', '', '<p>The two charts shown below are the sample chart canvas based on Chart.js. Chart.js is an easy, object oriented client side graphs for designers and developers. For more information how to create great looking charts using Chart.js, please visit <a href="http://www.chartjs.org/">Chart.js</a> homepage or download the script <a href="https://github.com/nnnick/Chart.js">here</a>.</p>\r\n\r\n<br />\r\n\r\n<div class="gantry-row">\r\n	<div class="gantry-width-container">\r\n		<div class="gantry-width-50">\r\n			<div class="gantry-width-spacer">\r\n				<canvas id="myChart-a" width="600" height="320"></canvas>\r\n\r\n				<script type="text/javascript">\r\n					var lineChartData = {\r\n						labels : ["Joomla","Wordpress","Magento","phpBB","Kunena","Email","Forum"],\r\n						datasets : [\r\n							{\r\n								fillColor : "transparent",\r\n								strokeColor : "#97DBF2",\r\n								pointColor : "#97DBF2",\r\n								pointStrokeColor : "#97DBF2",\r\n								data : [600,500,900,800,500,400,700]\r\n							},\r\n							{\r\n								fillColor : "transparent",\r\n								strokeColor : "#DE4E33",\r\n								pointColor : "#DE4E33",\r\n								pointStrokeColor : "#DE4E33",\r\n								data : [200,400,600,300,900,200,500]\r\n							},\r\n						]\r\n					},\r\n					options = {\r\n						//Boolean - If we want to override with a hard coded scale\r\n						scaleOverride : true,\r\n\r\n						//** Required if scaleOverride is true **\r\n						//Number - The number of steps in a hard coded scale\r\n						scaleSteps : 9,\r\n						//Number - The value jump in the hard coded scale\r\n						scaleStepWidth : 100,\r\n						//Number - The scale starting value\r\n						scaleStartValue : 100,\r\n\r\n						//String - Scale label font colour\r\n						scaleFontColor : "#FFFFFF",\r\n\r\n						//String - Colour of the scale line\r\n						scaleLineColor : "#FFFFFF",\r\n\r\n						//String - Colour of the grid lines\r\n						scaleGridLineColor : "#FFFFFF",\r\n\r\n						//Boolean - Whether the line is curved between points\r\n						bezierCurve : false,\r\n\r\n						//Number - Radius of each point dot in pixels\r\n						pointDotRadius : 8,\r\n\r\n						//Number - Pixel width of point dot stroke\r\n						pointDotStrokeWidth : 5,\r\n\r\n						//Number - Pixel width of dataset stroke\r\n						datasetStrokeWidth : 6,\r\n\r\n						//Boolean - Whether to fill the dataset with a colour\r\n						datasetFill : true,\r\n\r\n					}\r\n\r\n					//Get the context of the canvas element we want to select\r\n					var ctx = document.getElementById("myChart-a").getContext("2d");\r\n					var newChartA = new Chart(ctx).Line(lineChartData, options);\r\n\r\n				</script>\r\n			</div>\r\n		</div>\r\n\r\n		<div class="gantry-width-50">\r\n			<div class="gantry-width-spacer">\r\n				<canvas id="myChart-b" width="600" height="320"></canvas>\r\n\r\n				<script type="text/javascript">\r\n					var barChartData = {\r\n						labels : ["January","February","March","April","May","June","July"],\r\n						datasets : [\r\n							{\r\n								fillColor : "#D95240",\r\n								strokeColor : "#D95240",\r\n								data : [600,500,900,800,500,600,900]\r\n							},\r\n							{\r\n								fillColor : "#6FC6A0",\r\n								strokeColor : "#6FC6A0",\r\n								data : [700,300,800,700,600,500,800]\r\n							}\r\n						]\r\n					},\r\n					options = {\r\n						//Boolean - If we want to override with a hard coded scale\r\n						scaleOverride : true,\r\n					\r\n						//** Required if scaleOverride is true **\r\n						//Number - The number of steps in a hard coded scale\r\n						scaleSteps : 9,\r\n						//Number - The value jump in the hard coded scale\r\n						scaleStepWidth : 100,\r\n						//Number - The scale starting value\r\n						scaleStartValue : 100,\r\n\r\n						//String - Scale label font colour\r\n						scaleFontColor : "#FFFFFF",\r\n\r\n						//String - Colour of the scale line\r\n						scaleLineColor : "#FFFFFF",\r\n\r\n						//String - Colour of the grid lines\r\n						scaleGridLineColor : "#FFFFFF"\r\n					}\r\n\r\n					//Get the context of the canvas element we want to select\r\n					var ctx = document.getElementById("myChart-b").getContext("2d");\r\n					var newChartB = new Chart(ctx).Bar(barChartData, options);\r\n\r\n				</script>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n\r\n<div class="clear"></div><br />\r\n\r\n<p class="success">NOTE: The canvas only works on modern browsers (Firefox, Opera, Chrome, Safari, and Internet Explorer 9+) that support the HTML5 canvas element. <a target="_blank" href="http://www.w3schools.com/html/html5_canvas.asp">Internet Explorer 8 and earlier versions, do not support the canvas element.</a></p>', 1, 'maintop-a', 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'mod_custom', 1, 0, '{"prepare_content":"0","backgroundimage":"","layout":"_:default","moduleclass_sfx":"box2 nomarginall","cache":"1","cache_time":"900","cachemode":"static"}', 0, '*'),
